-- Create the users table
CREATE TABLE users (
    id INT PRIMARY KEY AUTO_INCREMENT,
    first_name VARCHAR(255) NOT NULL,
    last_name VARCHAR(255) NOT NULL,
    middle_name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    city VARCHAR(255) NOT NULL,
    barangay VARCHAR(255) NOT NULL,
    province VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL UNIQUE,
    contact_number VARCHAR(255) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL,
    account_type VARCHAR(255) NOT NULL,
    last_checkin_time VARCHAR(255),
    last_checkin_date VARCHAR(255),
    checkin_id VARCHAR(255)
);

-- Create the attendance table
CREATE TABLE attendance (
    attendance_id INT PRIMARY KEY AUTO_INCREMENT,
    user JSON NOT NULL,
    day_entered VARCHAR(45) NOT NULL,
    month_entered VARCHAR(45) NOT NULL,
    year_entered VARCHAR(45) NOT NULL,
    time_str_entered VARCHAR(45) NOT NULL,
    time_str_exit VARCHAR(45)
);

